import { getAddressList } from '@/api/address'
export default {
  namespaced: true,
  state () {
    return {
      addRessList: []
    }
  },
  mutations: {

  },
  actions: {
    async getAddressList (context) {
      const { data: { list } } = await getAddressList()
      context.state.addRessList = list[0]
    }

  },
  getters: {}

}
