import { get, set } from 'core-js/core/dict'

export default {
  namespaced: true,
  states () {
    return {
      // 个人权证相关
      userInfo: {
        token: '',
        userId: ''
      }
    }
  },
  mutations: {
    setUserInfo (state, obj) {
      state.userInfo = obj
    }
  },
  actions () {

  },
  getters () {

  }
}
