export default {
  namespaced: true,
  state () {
    return {
      cartList: []
    }
  },
  mutations: {
    setCartList () {

    }
  },
  actions: {
    async getCartAction (context) {
      const { data } = await getCartList()
      data.list.forEach(item => {
        item.ischecked = true
      })
      context.commit('setCartList', data.list)
    }

  },
  getters: {

  }
}
