import request from '@/utils/request'

export const getProList = (obj) => {
  const { categoryId, goodsName, page } = obj
  return request.get('/goods/list', {
    params: {
      categoryId,
      goodsName,
      page
    }
  })
}

// 获取商品详情数据
export const getProdetail = (goodsId) => {
  request.get('/goods/detail', {
    params: {
      goodsId
    }
  })
}
